package oops;

import java.util.Scanner;

public class Sumarray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


	int a[]=new int[4];
	Scanner sc=new Scanner(System.in);
	System.out.println("enter numbers");
	for(int i=0;i<4;i++)
		
	{
		a[i]=sc.nextInt();
	}
	
	int s=0;
	for(int v:a)
	{
		s=s+v;
		
	}
	System.out.println("sum="+s);
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	}

	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
}
